mapper.select:
SELECT suggest.*,
        employee.name AS creator,
        employee.room AS room
FROM suggest
LEFT JOIN employee ON suggest.creator_id = employee.account_id


        and suggest.name ILIKE CONCAT('%' || #{name} || '%')


        and "status" = #{status}


        and employee.name = #{creator}


        and employee.room = #{room}


        and create_date = #{createDate}



order by code ASC

    OFFSET (#{page} - 1) * #{limit} FETCH NEXT  #{limit} ROWS ONLY

SELECT COUNT(*)
FROM suggest
LEFT JOIN employee ON suggest.creator_id = employee.account_id



        and suggest.name ILIKE CONCAT('%' || #{name} || '%')


        and "status" = #{status}


        and employee.name = #{creator}


        and employee.room = #{room}


        and employee.unit = #{unit}


        and create_date = #{createDate}

SELECT COUNT(*) AS countSuggest, suggest.create_date AS createDate, suggest.status AS status, suggest.name AS name, employee.name AS creator, employee.room AS room, employee.unit AS unit FROM suggest LEFT JOIN employee ON suggest.creator_id = employee.account_id
and suggest.name ILIKE CONCAT('%' || #{name} || '%')
and "status" = #{status}
and employee.name = #{creator}
and employee.room = #{room}
and employee.unit = #{unit}
and create_date = #{createDate}
GROUP BY suggest.name, suggest.create_date, suggest.status, employee.name, employee.room, employee.unit order by create_date ASC
OFFSET (#{page} - 1) * #{limit} FETCH NEXT #{limit} ROWS ONLY
